def write_calendar_to_csv():
    try:
        import csv

        fieldnames = ["Year","Baisakh","Jestha","Ashar","Shrawan","Bhadra","Asoj","Kartik","Mangsir","Poush","Magh","Falgun","Chait"]
        with open('./data/calendar_bs.csv', 'w', encoding='UTF8', newline='') as f:
            writer = csv.DictWriter(f, fieldnames=fieldnames, delimiter=",")
            writer.writeheader()
            writer.writerows(CALENDAR_BS)
        return True
    except:
        return False